import React, { useState } from "react";
import "./Login.css";
import { Link } from "react-router-dom";
import { Form } from "react-bootstrap";
import axios from "axios";


const Login =() => {

    const handleSubmit = e =>{
        e.preventDefault();

        const data = {
            email: this. email,
            password: this.password
        }

        axios.post('localhost:8080/api/user/login', data).then(res => {
            console.log(res)
        }).catch(err =>{
            console.log(err)
        })
    }

    

    return (
        <div className="App">
            <div className="Box">
                <div className="HeaderContainer">
                    <div className="HeaderText">
                        Welcome!
                    </div>
                    <div className="small">
                        Please sign-in to continue!
                    </div>
                </div>    
            <div className="Backdrop">
                <div className="Top">
                </div>
            </div>
            <form onSubmit ={this.handleSubmit} className="Form FormClass">

                <input type="email" 
                    name="email" 
                    className="Input email" 
                    placeholder="Enter Your Email" 
                    onChange = {e => this.email = e.target.value}
                    required/>
                
                <input type="password" 
                    name="password" 
                    className="Input password" placeholder="Enter Your Password"
                    onChange = {e => this.password = e.target.value}
                    required/>
                
                <div className="MutedText">Forgot Your Password?</div>
                
                <button type="submit" name="btn" className="SubmitButton">Log In</button>

            </form>
            <Link to="/register" className="MutedText">Don't have an account? </Link>
            </div>
        </div>
    )
}

export default Login;
