import React from "react";
import Card from "react-bootstrap/Card";
import Button from 'react-bootstrap/Button';
import * as FaIcons from 'react-icons/fa';
import Form from 'react-bootstrap/Form';
import './pages.css';
import axios from "axios";
import {useState, useEffect} from "react";



const Home = () => {

	const [posts, setPosts] = useState([])

	useEffect( () =>{
		fetchData()
	}, [])

	const fetchData = async () => {
		const {data} = await axios.get("http://www.omdbapi.com/?t=blade")

		// http://www.omdbapi.com/?t=blade

		setPosts(data)
	}

	return (

		<div className="page">

			<div>				
			<Form className="searchContainer">
				<Form.Group>
					<Form.Control className="searchInput" type="text" placeholder="Search reviews" />
				</Form.Group>
				<Button id="searchIcon" variant="primary" type="submit">
					<FaIcons.FaSearch onclick="fetchData()" />
				</Button>
			</Form>	 
			</div>


			<div className="container">

				{posts.map(post => (
					<div key = {post}>

						<Card className="item" style={{ width: '18rem' }}>
							<Card.Title id="title">{post.Title}</Card.Title>
							<Card.Body id="body" > {post.Year} {post.Rated} {post.Runtime}</Card.Body>
							<Button id="button" variant="primary">Go somewhere</Button>
						</Card>


					</div>
				))}



			<Card className="item" style={{ width: '18rem' }}>
				<Card.Title id="title">Title</Card.Title>
				<Card.Body id="body" > Some quick example text to build on the card title and make up the bulk of the card's content.</Card.Body>
				<Button id="button" variant="primary">Go somewhere</Button>
			</Card>



			</div>
		</div>

	);
};

export default Home;
