import React, { Component } from 'react'
import {UserTopbarData, GuestTopbarData} from './TopbarData'
import './Topbar.css';
import axios from 'axios';

export default class Topbar extends Component {

  state = {};

  componentDidMount() {
    const auth = {
      headers: {
        Authorization: 'Bearer ' + localStorage.getItem('token')},
    }

  
        axios.get('http://localhost:8080/api/user/user', auth).then( (user) => {
      this.setState({
        user: user.data,
      })
    })
     
  }


  


  render(){

    if(this.state.user){
      return (<nav className="topbar">
      <ul className="topbar-nav">
        
        {UserTopbarData.map((val, key) => {
          return (
            <li 
              key={val.title}
              className="row"
              id={window.location.pathName == val.link ? "active" : ""}
              onClick={()=> {window.location.pathname = val.link;
              }}
            >
              <div id="title">{val.title}</div>
            </li>
          );
        })}
      </ul>
    </nav>
    );
    }
    return (
    //   <nav className="topbar">
    //     <ul className="topbar-nav"> { props.children } </ul>
    //   </nav>


<nav className="topbar">
      <ul className="topbar-nav">
        {GuestTopbarData.map((val, key) => {
          return (
            <li 
              key={val.title}
              className="row"
              id={window.location.pathName == val.link ? "active" : ""}
              onClick={()=> {window.location.pathname = val.link;
              }}
            >
              <div id="title">{val.title}</div>
            </li>
          );
        })}
      </ul>
    </nav>
    );
  }
}
