import React, { useState } from "react";
import "./Login.css";
import { Link } from "react-router-dom";
import { Form } from "react-bootstrap";
import axios from "axios";




const Login =() => {

    const [email, setEmail] = useState("");
    const [password, setPassword] =useState("");

    const [data, setData] = useState({
        email:"",
        password: ""
    });

    const {handleChange} = (e) =>{
        const value = e.target.value;
        setData({
            ...data,
            [e.target.name]:value
        });
    };

    const {handleSubmit, ...rest} = async (e) => {
        e.preventDefault();
        const userData = {
            email: data.email,
            password: data.password
        };

        axios.post("localhost:8080/api/user/login", userData).then((response) => {
            console.log(response);
        }).catch((error) => {
            if (error.response){
                console.log(error.response);
                console.log("server responded.")
            }else if(error.request) {
                console.log("network error")
            }
        })
    }

    return (
        <div className="App">
            <div className="Box">
                <div className="HeaderContainer">
                    <div className="HeaderText">
                        Welcome!
                    </div>
                    <div className="small">
                        Please sign-in to continue!
                    </div>
                </div>    
            <div className="Backdrop">
                <div className="Top">
                </div>
            </div>
            <form className="Form FormClass" handleSubmit = {handleSubmit}>

                <input type="email" 
                    name="email" 
                    defaultValue={data.email} 
                    onChange={(e)=>setEmail(e.target.value)} 
                    className="Input email" 
                    placeholder="Enter Your Email" required/>
                
                <input type="password" 
                    name="password" 
                    defaultValue={data.password} 
                    onChange={(e)=> setPassword(e.target.value)} 
                    className="Input password" placeholder="Enter Your Password" required/>
                
                <div className="MutedText">Forgot Your Password?</div>
                
                <button type="submit" name="btn" className="SubmitButton">Log In</button>

            </form>
            <Link to="/register" className="MutedText">Don't have an account? </Link>
            </div>
        </div>
    )
}

export default Login;
