import React, { useRef, useState, useEffect } from "react";
import "./SignupForm.css";
import Form from "bootstrap";
import { Link } from "react-router-dom";

const USER_REGEX = /^[A-z][A-z0-9-_]{3,23}$/;
const PWD_REGEX = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/;

const SignupForm = () => {
    const userRef = useRef();
    const errRef = useRef();

    const [user, setUser] = useState('');
    const [validName, setValidName] = useState(false);
    const [userFocus, setUserFocus] = useState(false);

    const [pwd, setPwd] = useState('');
    const [validPwd, setValidPWd] =useState(false);
    const [pwdFocus, setPwdFocus] = useState(false);
    
    const [matchPwd, setMatchPwd] = useState('');
    const [validMatch, setValidMatch] = useState(false);
    const [matchFocus, setMatchFocus] = useState(false);

    const [errMsg, setErrMsg] = useState('');
    const [success, setSuccess] = useState(false);

    useEffect(()=>{
        userRef.current.focus();
    }, [])

    //Username validation
    useEffect(()=> {
        const result = USER_REGEX.test(user);
        console.log(result);
        console.log(user);
        setValidName(result);
    },[user])

    //Password Validation
    useEffect(()=>{
        const result = PWD_REGEX.test(pwd);
        console.log(result);
        console.log(pwd);
        setValidPWd(result);
        const match = pwd === matchPwd;
        setValidMatch(match);
    }, [pwd, matchPwd])

    //Error Messaging
    useEffect(()=>{
        setErrMsg('');
    }, [user, pwd, matchPwd])

    return (  
        <section className="App">
            <div className="Box">
             <div className="HeaderContainer">
                <div className="HeaderText">
                    Welcome! <br />
                </div>
                <div className="small">
                            Please sign up to continue!
                </div>
            </div>    
            <div className="Backdrop">
                <div className="Top">
                </div>
            </div>
            <div className="Form FormContainer">
                <label htmlFor="email" hidden></label>
                <input type="email" name="email" className="Input email" placeholder="Please enter your email" required/>

                <label htmlFor="username" hidden>Username</label>
                    <input type="text" 
                    id="username"
                    name="username" 
                    ref={userRef}
                    autoComplete="off"
                    onChange={(e)=> setUser(e.target.value)}
                    aria-invalid={validName ? "false" : "true"}
                    aria-describedby="uidnote"
                    onFocus={()=> setUserFocus(true)}
                    onBlur={()=>setUserFocus(false)}
                    className="Input username" placeholder="Please enter your username" required/>
                     <p id="uidnote" className={userFocus && user && !validName ? "instructions" : "offscreen"}>
                        4 to 24 characters<br/>
                        Must begin with a letter. <br />
                        Letters, numbers, underscores, hyphens allowed.
                    </p>

                    <input type="password" name="psw" className="Input password" placeholder="Please enter your password" id="password"
                            onChange={(e) => setPwd(e.target.value)}
                            value={pwd}
                            required
                            aria-invalid={validPwd ? "false" : "true"}
                            aria-describedby="pwdnote"
                            onFocus={() => setPwdFocus(true)}
                            onBlur={() => setPwdFocus(false)}/>

                    <p id="pwdnote" className={pwdFocus && !validPwd ? "instructions" : "offscreen"}>
                         
                            8 to 24 characters.<br />
                            Must include uppercase and lowercase letters, a number and a special character.<br />
                            Allowed special characters: <span aria-label="exclamation mark">!</span> <span aria-label="at symbol">@</span> <span aria-label="hashtag">#</span> <span aria-label="dollar sign">$</span> <span aria-label="percent">%</span>
                        </p>

                    <input type="password" name="psw2" className="Input" placeholder="Please confirm your password"  id="confirm_pwd"
                            onChange={(e) => setMatchPwd(e.target.value)}
                            value={matchPwd}
                            required
                            aria-invalid={validMatch ? "false" : "true"}
                            aria-describedby="confirmnote"
                            onFocus={() => setMatchFocus(true)}
                            onBlur={() => setMatchFocus(false)}/>
                    <p id="confirmnote" className={matchFocus && !validMatch ? "instructions" : "offscreen"}>
                            
                            Must match the first password input field.
                        </p>
                </div>    
            <div className="MutedText">Forgot Your Password</div>
                <button type="submit" name="btn" className="SubmitButton" disabled={!validName || !validPwd || !validMatch ? true : false}>Sign Up</button>
                <Link to="/Login" className="MutedText">Already have an account? </Link>
        </div>
    </section>
    )
}
export default SignupForm;
